import React from 'react';
import { Modal, View, Text, TouchableOpacity, Linking, StyleSheet } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { colors } from '@/themes/Colors'; // ajuste o caminho conforme seu projeto

export default function UpdateModal({ visible, onClose, changelog, version, apkUrl }) {
  const handleUpdate = () => {
    Linking.openURL(apkUrl);
    onClose();
  };

  return (
    <Modal visible={visible} animationType="fade" transparent>
      <View style={styles.overlay}>
        <View style={styles.container}>
          <Ionicons name="rocket-outline" size={50} color={colors.primary || '#4CAF50'} />
          <Text style={styles.title}>Nova versÃ£o disponÃ­vel ðŸš€</Text>

          <Text style={styles.subtitle}>VersÃ£o {version}</Text>

          <Text style={styles.changelog}>{changelog}</Text>

          <View style={styles.buttons}>
            <TouchableOpacity style={[styles.button, styles.later]} onPress={onClose}>
              <Text style={styles.laterText}>Mais tarde</Text>
            </TouchableOpacity>

            <TouchableOpacity style={[styles.button, styles.update]} onPress={handleUpdate}>
              <Text style={styles.updateText}>Atualizar agora</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </Modal>
  );
}

const styles = StyleSheet.create({
  overlay: {
    flex: 1,
    backgroundColor: 'rgba(0,0,0,0.7)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    width: '85%',
    backgroundColor: '#fff',
    borderRadius: 16,
    padding: 20,
    alignItems: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#333',
    marginTop: 10,
    textAlign: 'center',
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    marginBottom: 10,
  },
  changelog: {
    fontSize: 15,
    color: '#444',
    textAlign: 'center',
    marginVertical: 10,
  },
  buttons: {
    flexDirection: 'row',
    marginTop: 15,
  },
  button: {
    flex: 1,
    marginHorizontal: 5,
    borderRadius: 8,
    paddingVertical: 10,
    alignItems: 'center',
  },
  later: {
    backgroundColor: '#ccc',
  },
  update: {
    backgroundColor: '#4CAF50',
  },
  laterText: {
    color: '#333',
    fontWeight: '600',
  },
  updateText: {
    color: '#fff',
    fontWeight: '600',
  },
});
